CREATE TABLE Nauczyciele(pesel VARCHAR(11) PRIMARY KEY NOT NULL, imie VARCHAR(45), nazwisko VARCHAR(45), stopienNaukowy VARCHAR(45), stawkaGodzinowa INT DEFAULT 25, CHECK (stawkaGodzinowa>=17));
CREATE TABLE Lekcje(id INT PRIMARY KEY NOT NULL AUTO_INCREMENT, jezyk VARCHAR(45), data DATETIME, stopien ENUM('A1', 'A2', 'B1', 'B2', 'C1', 'C2'), podrecznik VARCHAR(11));
CREATE TABLE LekcjeNauczycieli(pesel VARCHAR(11), idLekcji INT, PRIMARY KEY(pesel, idLekcji), FOREIGN KEY (pesel) REFERENCES Nauczyciele(pesel), FOREIGN KEY (idLekcji) REFERENCES Lekcje(id));
CREATE TABLE Uczniowie(pesel VARCHAR(11) PRIMARY KEY NOT NULL, imie VARCHAR(45), nazwisko VARCHAR(45));
CREATE TABLE LekcjeUczniow(idLekcji INT, pesel VARCHAR(11), PRIMARY KEY(idLekcji, pesel), FOREIGN KEY (idLekcji) REFERENCES Lekcje(id) ON DELETE CASCADE, FOREIGN KEY (pesel) REFERENCES Uczniowie(pesel) ON DELETE CASCADE);
CREATE TABLE Platnosci(id INT PRIMARY KEY NOT NULL AUTO_INCREMENT, pesel VARCHAR(11), semestr VARCHAR(11),kwota INT, jezyk VARCHAR(11), FOREIGN KEY (pesel) REFERENCES Uczniowie(pesel));
CREATE TABLE Egzaminy(id INT PRIMARY KEY NOT NULL AUTO_INCREMENT, nazwa VARCHAR(45), data DATETIME, jezyk VARCHAR(11));
CREATE TABLE EgzaminyUczniow(idEgzaminu INT, pesel VARCHAR(11), zaliczenie ENUM('1', '0'), PRIMARY KEY(idEgzaminu, pesel), FOREIGN KEY (pesel) REFERENCES Uczniowie(pesel), FOREIGN KEY (idEgzaminu) REFERENCES Egzaminy(id));